sql.where:
r.status = 3 AND r.attendance_state = 1
AND r.creator_code = #{userCode}
AND r.org_index_path LIKE CONCAT(#{adminIndexPath},'%')
AND (
r.org_index_path LIKE CONCAT(#{item},'%') OR
1 = 2 )
AND p.org_index IN
#{item}
AND r.practice_status = #{practiceStatus}
AND r.start_date <=#{endDate}
AND r.end_date >= #{startDate}
AND p.person_name like CONCAT('%',#{search},'%')
AND ( p.person_no LIKE CONCAT('%',#{search},'%') OR p.person_name like CONCAT('%',#{search},'%') OR r.practice_company LIKE CONCAT('%',#{search},'%') OR r.company_contacts LIKE CONCAT('%',#{search},'%') )

mapper.select:
SELECT person_no
FROM practice_persons
WHERE practice_record_id = #{recordId}

SELECT practice_record_id AS id, string_agg(person_name, '„ÄÅ' ORDER BY id) AS value FROM practice_persons WHERE practice_record_id IN
#{item}
GROUP BY practice_record_id ORDER BY practice_record_id DESC

SELECT practice_record_id AS id, person_url AS value FROM ( SELECT pp.practice_record_id, p.person_url, "row_number"() OVER ( PARTITION BY practice_record_id ORDER BY pp.id ) rn FROM practice_persons pp LEFT JOIN persons AS p ON p.person_no = pp.person_no WHERE pp.practice_record_id IN
#{item}
AND p.person_url is not null) t WHERE t.rn = 1

SELECT pp.person_no
FROM practice_persons AS pp
INNER JOIN practice_records as pr ON pr.id = pp.practice_record_id
WHERE pr.start_date <= #{date}
AND pr.end_date >= #{date}
AND person_no IN

    #{personNo}

AND pr.status = 3

SELECT pp.practice_record_id, pp.id AS practice_person_id, pp.person_no FROM practice_persons pp INNER JOIN practice_records pr on pr.id = pp.practice_record_id WHERE pp.person_no IN
#{personNo}
AND pr.status = 2 AND pr.attendance_state = 0 AND pr.reported_at <= to_timestamp(#{attendanceDate} || ' 23:59:59.999', 'yyyy-MM-dd HH24:MI:SS.MS') AND pr.start_date >= #{attendanceEndDate}

update.foreach:
UPDATE "practice_persons"
SET attendance_result = #{item.attendanceResult},
attendance_time = #{item.attendanceTime}
WHERE id = #{item.practicePersonId}

mapper.select:
SELECT pp.person_no, p.person_name, p.org_name AS class_name, COALESCE(pp.attendance_result, -1) AS attendance_result, pp.attendance_time :: DATE AS attendance_date, pp.attendance_time FROM practice_persons pp INNER JOIN persons P ON P.person_no = pp.person_no WHERE pp.practice_record_id = #{recordId}
AND pp.attendance_result is null
AND pp.attendance_result = #{attendanceResult}
AND (pp.person_name LIKE CONCAT('%', #{search}, '%') OR pp.person_no LIKE CONCAT('%', #{search}, '%') OR p.org_name LIKE CONCAT('%', #{search}, '%'))
ORDER BY ${orderField}
${orderRule}
ORDER BY pp.id

SELECT pp.person_no, pp.person_name, p.person_url, r.id AS record_id, r.reported_at::DATE AS attendance_start_date, DATE(r.start_date - INTERVAL '1 day') AS attendance_end_date, COALESCE(pp.attendance_result, -1) AS attendance_result, pp.attendance_time, r.reported_at FROM practice_persons pp INNER JOIN practice_records r ON pp.practice_record_id = r.id INNER JOIN persons p ON p.person_no = pp.person_no WHERE r.creator_code = #{userCode} AND r.status >= 2
AND pp.attendance_result is null
AND pp.attendance_result = #{attendanceResult}
AND r.reported_at >= #{reportTimeMin}
AND r.reported_at <= #{reportTimeMax}
AND (pp.person_name LIKE CONCAT('%', #{search}, '%') OR pp.person_no LIKE CONCAT('%', #{search}, '%'))
ORDER BY pp.attendance_result DESC, pp.id

mapper.update:
UPDATE "practice_persons"
SET read = true
WHERE id = #{id}

mapper.select:
SELECT practice_record_id
FROM practice_records r
         inner join practice_persons p on p.practice_record_id = r.id
where p.person_no = #{personNo}
  and end_date >= #{date}
  and #{date} >= start_date
